import React, { useState, useEffect } from "react";
import { Calendar, Users, Search, Star, MapPin, Phone, Mail, User, CreditCard, Shield, Check, Clock, Wifi, Car, Coffee, Utensils, Waves, Mountain } from "lucide-react";

const HotelReservationSystem = () => {
  const [currentStep, setCurrentStep] = useState('search');
  const [searchData, setSearchData] = useState({
    checkIn: '',
    checkOut: '',
    guests: 2,
    rooms: 1
  });
  
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [bookingData, setBookingData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    specialRequests: ''
  });
  
  const [focusedField, setFocusedField] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [nights, setNights] = useState(0);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [reservationId, setReservationId] = useState(null);

  // Simulation des chambres disponibles
  const availableRooms = [
    { 
      id: 'classic', 
      name: 'Chambre Classique', 
      price: 185, 
      originalPrice: 220,
      image: 'ðŸ›ï¸',
      size: '25mÂ²',
      capacity: 2,
      features: ['Vue jardin', 'Wifi gratuit', 'Minibar', 'Bureau'],
      amenities: [Wifi, Coffee, Car],
      description: 'Chambre Ã©lÃ©gante avec vue sur nos jardins luxuriants',
      available: 3
    },
    { 
      id: 'deluxe', 
      name: 'Suite Deluxe', 
      price: 350, 
      originalPrice: 420,
      image: 'ðŸ–ï¸',
      size: '45mÂ²',
      capacity: 3,
      features: ['Vue mer panoramique', 'Balcon privÃ©', 'Service Ã©tage 24h', 'Salon sÃ©parÃ©'],
      amenities: [Waves, Wifi, Utensils, Car],
      description: 'Suite spacieuse avec vue imprenable sur la mer',
      available: 2
    },
    { 
      id: 'presidential', 
      name: 'Suite PrÃ©sidentielle', 
      price: 750, 
      originalPrice: 950,
      image: 'ðŸ°',
      size: '85mÂ²',
      capacity: 4,
      features: ['Terrasse privÃ©e 20mÂ²', 'Jacuzzi', 'Butler personnel', 'Salon & salle Ã  manger'],
      amenities: [Mountain, Waves, Utensils, Car],
      description: 'Le summum du luxe avec services personnalisÃ©s',
      available: 1
    }
  ];

  // Calculer le nombre de nuits
  useEffect(() => {
    if (searchData.checkIn && searchData.checkOut) {
      const checkIn = new Date(searchData.checkIn);
      const checkOut = new Date(searchData.checkOut);
      const timeDiff = checkOut.getTime() - checkIn.getTime();
      const nightCount = Math.ceil(timeDiff / (1000 * 3600 * 24));
      setNights(nightCount > 0 ? nightCount : 0);
    }
  }, [searchData.checkIn, searchData.checkOut]);

  const handleSearchSubmit = async () => {
    if (!searchData.checkIn || !searchData.checkOut) {
      alert('Veuillez sÃ©lectionner vos dates de sÃ©jour');
      return;
    }
    
    setIsSearching(true);
    // Simulation d'une recherche
    await new Promise(resolve => setTimeout(resolve, 1500));
    setIsSearching(false);
    setCurrentStep('rooms');
  };

  const handleRoomSelect = (room) => {
    setSelectedRoom(room);
    setCurrentStep('booking');
  };

  const handleBookingSubmit = async () => {
    // Validation des champs obligatoires
    if (!bookingData.firstName || !bookingData.lastName || !bookingData.email || !bookingData.phone) {
      alert('Veuillez remplir tous les champs obligatoires');
      return;
    }

    // Validation de l'email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(bookingData.email)) {
      alert('Veuillez entrer une adresse email valide');
      return;
    }

    const reservationData = {
      firstName: bookingData.firstName,
      lastName: bookingData.lastName,
      email: bookingData.email,
      phone: bookingData.phone,
      specialRequests: bookingData.specialRequests,
      roomType: selectedRoom?.name,
      checkIn: searchData.checkIn,
      checkOut: searchData.checkOut,
      guests: searchData.guests,
      rooms: searchData.rooms,
      totalPrice: getTotalPrice()
    };

    try {
      setIsSubmitting(true);
      const response = await fetch("http://localhost:5000/api/reservations", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(reservationData),
      });

      const result = await response.json();

      if (response.ok && result.success) {
        setReservationId(result.reservationId);
        setCurrentStep('confirmation');
      } else {
        alert(result.message || "Erreur lors de la rÃ©servation !");
      }
    } catch (error) {
      console.error("Erreur lors de l'envoi :", error);
      alert("Erreur de connexion au serveur. Veuillez vÃ©rifier que le serveur backend est dÃ©marrÃ©.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const getTotalPrice = () => {
    if (!selectedRoom) return 0;
    return selectedRoom.price * nights * searchData.rooms;
  };

  // Ã‰tape 1: Recherche
  if (currentStep === 'search') {
    return (
      <div className="min-h-screen" style={{background: 'linear-gradient(135deg, #5a4432 0%, #b1762c 50%, #e8dcc9 100%)'}} className="relative overflow-hidden">
        {/* Particules flottantes */}
        <div className="absolute inset-0">
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="absolute w-2 h-2 bg-white/20 rounded-full animate-pulse"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                animationDelay: `${Math.random() * 2}s`,
                animationDuration: `${2 + Math.random() * 2}s`
              }}
            />
          ))}
        </div>

        <div className="relative z-10 min-h-screen flex items-center justify-center p-4">
          <div className="max-w-4xl w-full">
            {/* Header */}
            <div className="text-center mb-12">
              <div className="inline-flex items-center gap-3 mb-6">
                <div className="w-16 h-16 rounded-full flex items-center justify-center shadow-2xl" style={{background: 'linear-gradient(135deg, #b1762c 0%, #e8dcc9 100%)'}}>
                  <Star className="w-8 h-8" style={{color: '#fcf9f2'}} />
                </div>
                <h1 className="text-5xl font-light tracking-wide" style={{color: '#fcf9f2'}}>ChÃ¢teau Ã‰lÃ©gance</h1>
              </div>
              <p className="text-xl font-light" style={{color: '#fcf9f2', opacity: 0.9}}>DÃ©couvrez l'art de l'hospitalitÃ© raffinÃ©e</p>
            </div>

            {/* Formulaire de recherche */}
           <div
  className="rounded-3xl p-8 border shadow-xl"
  style={{
    backgroundColor: '#f6efe2',
    borderColor: '#e2d6c3',
  }}
>
  <div className="text-center mb-8">
    <h2 className="text-2xl font-semibold mb-2" style={{ color: '#5a3a1e' }}>
      Trouvez votre sÃ©jour parfait
    </h2>
    <p style={{ color: '#7a5a3a' }}>
      SÃ©lectionnez vos dates et prÃ©fÃ©rences
    </p>
  </div>

  <div className="grid lg:grid-cols-5 gap-6 mb-8">
    {/* ArrivÃ©e */}
    <div className="relative">
      <input
        type="date"
        value={searchData.checkIn}
        onChange={(e) =>
          setSearchData((prev) => ({ ...prev, checkIn: e.target.value }))
        }
        min={new Date().toISOString().split('T')[0]}
        className="w-full h-16 px-4 pt-6 pb-2 border rounded-2xl focus:outline-none"
        style={{
          backgroundColor: '#fbf8f2',
          borderColor: '#d7c5af',
          color: '#5a3a1e',
        }}
      />
      <label
        className="absolute left-4 top-2 text-xs font-medium"
        style={{ color: '#b1762c' }}
      >
        ArrivÃ©e
      </label>
    </div>

    {/* DÃ©part */}
    <div className="relative">
      <input
        type="date"
        value={searchData.checkOut}
        onChange={(e) =>
          setSearchData((prev) => ({ ...prev, checkOut: e.target.value }))
        }
        min={searchData.checkIn || new Date().toISOString().split('T')[0]}
        className="w-full h-16 px-4 pt-6 pb-2 border rounded-2xl focus:outline-none"
        style={{
          backgroundColor: '#fbf8f2',
          borderColor: '#d7c5af',
          color: '#5a3a1e',
        }}
      />
      <label
        className="absolute left-4 top-2 text-xs font-medium"
        style={{ color: '#b1762c' }}
      >
        DÃ©part
      </label>
    </div>

    {/* InvitÃ©s */}
    <div className="relative">
      <select
        value={searchData.guests}
        onChange={(e) =>
          setSearchData((prev) => ({
            ...prev,
            guests: parseInt(e.target.value),
          }))
        }
        className="w-full h-16 px-4 pt-6 pb-2 border rounded-2xl appearance-none focus:outline-none"
        style={{
          backgroundColor: '#fbf8f2',
          borderColor: '#d7c5af',
          color: '#5a3a1e',
        }}
      >
        {[1, 2, 3, 4, 5, 6, 7, 8].map((num) => (
          <option
            key={num}
            value={num}
            style={{
              backgroundColor: '#f6efe2',
              color: '#5a3a1e',
            }}
          >
            {num} {num === 1 ? 'personne' : 'personnes'}
          </option>
        ))}
      </select>
      <label
        className="absolute left-4 top-2 text-xs font-medium"
        style={{ color: '#b1762c' }}
      >
        InvitÃ©s
      </label>
    </div>

    {/* Chambres */}
    <div className="relative">
      <select
        value={searchData.rooms}
        onChange={(e) =>
          setSearchData((prev) => ({
            ...prev,
            rooms: parseInt(e.target.value),
          }))
        }
        className="w-full h-16 px-4 pt-6 pb-2 border rounded-2xl appearance-none focus:outline-none"
        style={{
          backgroundColor: '#fbf8f2',
          borderColor: '#d7c5af',
          color: '#5a3a1e',
        }}
      >
        {[1, 2, 3, 4, 5].map((num) => (
          <option
            key={num}
            value={num}
            style={{
              backgroundColor: '#f6efe2',
              color: '#5a3a1e',
            }}
          >
            {num} {num === 1 ? 'chambre' : 'chambres'}
          </option>
        ))}
      </select>
      <label
        className="absolute left-4 top-2 text-xs font-medium"
        style={{ color: '#b1762c' }}
      >
        Chambres
      </label>
    </div>

    {/* Bouton Rechercher */}
    <button
      onClick={handleSearchSubmit}
      disabled={isSearching}
      className="h-16 px-8 rounded-2xl font-medium transform hover:scale-105 transition-all duration-300 flex items-center justify-center gap-3 disabled:opacity-50 disabled:cursor-not-allowed"
      style={{
        background: 'linear-gradient(to right, #b1762c, #7e5d3c)',
        color: '#fefbf7',
        boxShadow: '0 12px 24px rgba(177, 118, 44, 0.3)',
      }}
    >
      {isSearching ? (
        <>
          <div
            className="w-5 h-5 border-2 border-t-transparent rounded-full animate-spin"
            style={{
              borderColor: 'rgba(90, 68, 50, 0.3)',
              borderTopColor: '#fff',
            }}
          />
          Recherche...
        </>
      ) : (
        <>
          <Search className="w-5 h-5" />
          Rechercher
        </>
      )}
    </button>
  </div>

  {/* RÃ©sumÃ© sÃ©jour */}
  {nights > 0 && (
    <div className="text-center" style={{ color: '#7a5a3a' }}>
      <p className="text-lg">
        <span className="font-medium" style={{ color: '#b1762c' }}>
          {nights}
        </span>{' '}
        {nights === 1 ? 'nuit' : 'nuits'}
        <span className="mx-2">â€¢</span>
        <span className="font-medium" style={{ color: '#b1762c' }}>
          {searchData.guests}
        </span>{' '}
        {searchData.guests === 1 ? 'personne' : 'personnes'}
        <span className="mx-2">â€¢</span>
        <span className="font-medium" style={{ color: '#b1762c' }}>
          {searchData.rooms}
        </span>{' '}
        {searchData.rooms === 1 ? 'chambre' : 'chambres'}
      </p>
    </div>
  )}
</div>

            {/* Contact info */}
            <div className="text-center mt-12">
              <div className="flex items-center justify-center gap-8 text-sm" style={{color: 'rgba(252, 249, 242, 0.6)'}}>
                <div className="flex items-center gap-2">
                  <Phone className="w-4 h-4" />
                  <span>+33 1 23 45 67 89</span>
                </div>
                <div className="flex items-center gap-2">
                  <Mail className="w-4 h-4" />
                  <span>contact@chateau-elegance.com</span>
                </div>
                <div className="flex items-center gap-2">
                  <MapPin className="w-4 h-4" />
                  <span>CÃ´te d'Azur, France</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Ã‰tape 2: SÃ©lection des chambres
  if (currentStep === 'rooms') {
    return (
      <div className="min-h-screen p-4" style={{background: 'linear-gradient(135deg, #fcf9f2 0%, #f5f2ed 100%)'}}>
        <div className="max-w-6xl mx-auto">
          {/* Header avec rÃ©sumÃ© de recherche */}
